import React, { useState, useEffect } from 'react';
import { getAlunos } from '../services/api';

const AlunosList = () => {
  const [alunos, setAlunos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAlunos = async () => {
      try {
        setLoading(true);
        const data = await getAlunos();
        setAlunos(data);
        setError(null);
      } catch (err) {
        setError('Erro ao carregar alunos');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchAlunos();
  }, []);

  const containerStyle = {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px'
  };

  const titleStyle = {
    fontSize: '2rem',
    marginBottom: '2rem',
    color: '#64B5F6'
  };

  const loadingStyle = {
    textAlign: 'center',
    padding: '2rem',
    fontSize: '1.2rem'
  };

  const errorStyle = {
    textAlign: 'center',
    padding: '2rem',
    color: '#f44336',
    fontSize: '1.2rem'
  };

  const tableContainerStyle = {
    overflowX: 'auto'
  };

  if (loading) {
    return <div style={loadingStyle}>Carregando alunos...</div>;
  }

  if (error) {
    return <div style={errorStyle}>{error}</div>;
  }

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>Lista de Alunos</h1>
      
      <div style={tableContainerStyle}>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Idade</th>
              <th>Curso</th>
              <th>Matrícula</th>
              <th>Email</th>
            </tr>
          </thead>
          <tbody>
            {alunos.map((aluno) => (
              <tr key={aluno.idaluno}>
                <td>{aluno.idaluno}</td>
                <td>{aluno.nome}</td>
                <td>{aluno.idade}</td>
                <td>{aluno.curso}</td>
                <td>{aluno.matricula}</td>
                <td>{aluno.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {alunos.length === 0 && (
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          Nenhum aluno encontrado.
        </div>
      )}
    </div>
  );
};

export default AlunosList;
