import React from 'react';

const Header = () => {
  const headerStyle = {
    backgroundColor: '#1E1E1E',
    padding: '1rem 2rem',
    boxShadow: '0 2px 4px rgba(0, 0, 0, 0.3)',
    borderBottom: '1px solid #333'
  };

  const navStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    maxWidth: '1200px',
    margin: '0 auto'
  };

  const logoStyle = {
    fontSize: '1.5rem',
    fontWeight: 'bold',
    color: '#64B5F6'
  };

  const navLinksStyle = {
    display: 'flex',
    gap: '2rem',
    listStyle: 'none'
  };

  const linkStyle = {
    color: '#FFFFFF',
    textDecoration: 'none',
    padding: '0.5rem 1rem',
    borderRadius: '4px',
    transition: 'all 0.3s ease',
    backgroundColor: 'transparent'
  };

  return (
    <header style={headerStyle}>
      <nav style={navStyle}>
        <div style={logoStyle}>
          Sistema de Alunos
        </div>
        <ul style={navLinksStyle}>
          <li>
            <a href="#" style={linkStyle}>
              Home
            </a>
          </li>
          <li>
            <a href="#" style={linkStyle}>
              Alunos
            </a>
          </li>
        </ul>
      </nav>
    </header>
  );
};

export default Header;
