import React, { useState, useEffect } from 'react';
import { getAlunos, createAluno, updateAluno, deleteAluno } from '../services/api';

const AlunosList = () => {
  const [alunos, setAlunos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [form, setForm] = useState({ nome: '', idade: '', curso: '', matricula: '', email: '' });
  const [editId, setEditId] = useState(null);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchAlunos();
  }, []);

  const fetchAlunos = async () => {
    setLoading(true);
    try {
      const data = await getAlunos();
      setAlunos(data);
      setError(null);
    } catch (err) {
      setError('Erro ao carregar alunos');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editId) {
        await updateAluno(editId, form);
      } else {
        await createAluno(form);
      }
      setForm({ nome: '', idade: '', curso: '', matricula: '', email: '' });
      setEditId(null);
      setShowForm(false);
      fetchAlunos();
    } catch (err) {
      setError('Erro ao salvar aluno');
    }
  };

  const handleEdit = (aluno) => {
    setForm({
      nome: aluno.nome,
      idade: aluno.idade,
      curso: aluno.curso,
      matricula: aluno.matricula,
      email: aluno.email
    });
    setEditId(aluno.idaluno);
    setShowForm(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Tem certeza que deseja excluir este aluno?')) {
      try {
        await deleteAluno(id);
        fetchAlunos();
      } catch (err) {
        setError('Erro ao excluir aluno');
      }
    }
  };

  const handleAdd = () => {
    setForm({ nome: '', idade: '', curso: '', matricula: '', email: '' });
    setEditId(null);
    setShowForm(true);
  };

  const handleCancel = () => {
    setForm({ nome: '', idade: '', curso: '', matricula: '', email: '' });
    setEditId(null);
    setShowForm(false);
  };

  const containerStyle = {
    maxWidth: '1200px',
    margin: '0 auto',
    padding: '20px'
  };

  const titleStyle = {
    fontSize: '2rem',
    marginBottom: '2rem',
    color: '#64B5F6'
  };

  const buttonStyle = {
    backgroundColor: '#1976D2',
    color: '#FFF',
    border: 'none',
    borderRadius: '4px',
    padding: '8px 16px',
    margin: '0 4px',
    cursor: 'pointer'
  };

  if (loading) {
    return <div style={{ textAlign: 'center', padding: '2rem' }}>Carregando alunos...</div>;
  }

  if (error) {
    return <div style={{ textAlign: 'center', padding: '2rem', color: '#f44336' }}>{error}</div>;
  }

  return (
    <div style={containerStyle}>
      <h1 style={titleStyle}>Lista de Alunos</h1>
      <button style={buttonStyle} onClick={handleAdd}>Adicionar Aluno</button>

      {showForm && (
        <form onSubmit={handleSubmit} style={{ margin: '2rem 0', background: '#222', padding: '1rem', borderRadius: '8px' }}>
          <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
            <input name="nome" placeholder="Nome" value={form.nome} onChange={handleInputChange} required />
            <input name="idade" placeholder="Idade" value={form.idade} onChange={handleInputChange} required type="number" min="0" />
            <input name="curso" placeholder="Curso" value={form.curso} onChange={handleInputChange} required />
            <input name="matricula" placeholder="Matrícula" value={form.matricula} onChange={handleInputChange} required />
            <input name="email" placeholder="Email" value={form.email} onChange={handleInputChange} required type="email" />
          </div>
          <div style={{ marginTop: '1rem' }}>
            <button type="submit" style={buttonStyle}>{editId ? 'Salvar Alterações' : 'Adicionar'}</button>
            <button type="button" style={{ ...buttonStyle, backgroundColor: '#888' }} onClick={handleCancel}>Cancelar</button>
          </div>
        </form>
      )}

      <div style={{ overflowX: 'auto' }}>
        <table>
          <thead>
            <tr>
              <th>ID</th>
              <th>Nome</th>
              <th>Idade</th>
              <th>Curso</th>
              <th>Matrícula</th>
              <th>Email</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {alunos.map((aluno) => (
              <tr key={aluno.idaluno}>
                <td>{aluno.idaluno}</td>
                <td>{aluno.nome}</td>
                <td>{aluno.idade}</td>
                <td>{aluno.curso}</td>
                <td>{aluno.matricula}</td>
                <td>{aluno.email}</td>
                <td>
                  <button style={buttonStyle} onClick={() => handleEdit(aluno)}>Editar</button>
                  <button style={{ ...buttonStyle, backgroundColor: '#f44336' }} onClick={() => handleDelete(aluno.idaluno)}>Excluir</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {alunos.length === 0 && (
        <div style={{ textAlign: 'center', padding: '2rem' }}>
          Nenhum aluno encontrado.
        </div>
      )}
    </div>
  );
};

export default AlunosList;
